      stroke: @subtext0;
    }

    /* Text */

    .\!text-black {
      color: @black !important;
    }

    .text-\[\#FE7600\] {
      color: mix(@yellow, @red);
    }

    .text-\[\#fff\] {
      color: @base;
    }

    .text-black {
