    }

    .text-black\/60 {
      color: fade(@black, 60%);
    }

    .text-black\/70 {
      color: fade(@black, 70%);
    }

    .text-blue-500 {
      color: lighten(@blue, 10%);
    }

    .text-blue-600 {
      color: @blue;
    }

    .text-brand-blue-800 {
      color: @blue;
    }

    .text-brand-purple {
      color: @accent;
    }

