@-moz-document domain("m.youtube.com") {
  html[darker-dark-theme] {
    #catppuccin(@darkFlavor);
  }
  html {
    #catppuccin(@lightFlavor);
  }

  #catppuccin(@flavor) {
    #lib.palette();

    background-color: @base !important;
    color: @text !important;

    /* Ambient mode */
    #cinematics {
      mix-blend-mode: lighten;
    }

    .mobile-topbar-header[data-mode="watch"],
    ytm-mobile-topbar-renderer:not([ambient-topbar]) {
      background: @base;
    }

    .mobile-topbar-header[data-mode="watch"] .mobile-topbar-logo,
    .mobile-topbar-header[data-mode="watch"] .mobile-topbar-title,
    .mobile-topbar-header[data-mode="watch"] .mobile-topbar-header-content {
      color: @text;
    }

    .yt-spec-bottom-sheet-layout__bottom-sheet-renderer-container,
    .modern-styling {
      background: @surface0;
    }

    .yt-spec-bottom-sheet-layout__bottom-sheet-layout-header-wrapper {
      border-bottom-color: @surface0;
    }

    .yt-spec-bottom-sheet-layout__bottom-sheet-content {
      color: @text;
    }

    /* Comment box */
    .engagement-panel-container {
      background: @mantle;
    }

    /* Bottom bar */
    ytm-pivot-bar-renderer {
      background: @mantle;
      color: @text;
      border-top-color: @surface0;
    }

    /* Chips */
    .chip-bar {
      background-color: @base;
    }

    [chip-style*="STYLE_"] {
      .chip-container {
        color: @text;
        background-color: @surface0;
      }

      &.selected .chip-container {
        color: @crust;
        background-color: @accent;
      }
    }

    /* Buttons */

    .yt-spec-touch-feedback-shape--touch-response {
      .yt-spec-touch-feedback-shape__fill {
        background-color: @text;
      }

      .yt-spec-touch-feedback-shape__stroke {
        border-color: @surface1;
      }
    }

    .yt-spec-touch-feedback-shape--overlay-touch-response-inverse
      .yt-spec-touch-feedback-shape__fill {
      background-color: @accent;
    }
    .yt-spec-touch-feedback-shape--overlay-touch-response
      .yt-spec-touch-feedback-shape__fill {
      background-color: @text;
    }
  }
}

/* ==UserStyle==
@name           Seedr Catppuccin Mocha
@namespace      github.com/openstyles/stylus
@version        1.3.0
@description    Catppuccin Mocha dark theme for Seedr.cc
@author         Me
==/UserStyle== */
@-moz-document url-prefix("https://www.seedr.cc") {

    /* =====================
     CATPPUCCIN VARIABLES
     ===================== */
    :root {
        color-scheme: dark;

        --ctp-base: #1e1e2e;
        --ctp-mantle: #181825;
        --ctp-crust: #11111b;

        --ctp-surface0: #313244;
        --ctp-surface1: #45475a;
        --ctp-surface2: #585b70;

        --ctp-text: #cdd6f4;
        --ctp-subtext1: #bac2de;
        --ctp-subtext0: #a6adc8;

        --ctp-mauve: #cba6f7;
        --ctp-pink: #f5c2e7;
        --ctp-red: #f38ba8;
        --ctp-blue: #89b4fa;
        --ctp-green: #a6e3a1;
    }

    /* =====================
     GLOBAL BASE
     ===================== */
    body,
    .main-bg,
    .page-header,
    footer,
    #files-heading {
        background-color: var(--ctp-base) !important;
        color: var(--ctp-text) !important;
    }

    a {
        color: var(--ctp-mauve) !important;
    }

    a:hover {
        color: var(--ctp-pink) !important;
    }

    /* =====================
     FILE LIST
     ===================== */
    .row.content-item {
        background-color: var(--ctp-surface0) !important;
    }

    .content-item,
    .content-item a,
    .content-item span {
        color: var(--ctp-text) !important;
    }

    .content-item.folder,
    .content-item.folder a,
    .content-item.folder span {
        color: var(--ctp-mauve) !important;
    }

    .content-item:hover,
    .content-item:hover a,
    .content-item:hover span {
        background-color: var(--ctp-surface1) !important;
        color: #ffffff !important;
    }

    /* Folder icon */
    .s-icon-column .folder-icon {
        background-color: var(--ctp-mauve) !important;
        color: var(--ctp-mauve) !important;
        fill: var(--ctp-mauve) !important;
    }

    /* =====================
     SIZE COLUMN (REAL FIX)
     ===================== */
    .content-item-size {
        background-color: var(--ctp-surface0) !important;
        color: var(--ctp-text) !important;
        text-align: center;
        font-weight: 500;
    }

    .content-item:hover .content-item-size {
        background-color: var(--ctp-surface1) !important;
        color: #ffffff !important;
    }

    /* =====================
     TABLE HEADERS
     ===================== */
    .table-heading {
        background-color: var(--ctp-surface0) !important;
        color: var(--ctp-text) !important;
        border-bottom: 3px solid var(--ctp-surface1) !important;
    }

    /* =====================
     INPUTS
     ===================== */
    input,
    textarea,
    select {
        background-color: var(--ctp-mantle) !important;
        color: #ffffff !important;
        border: 1px solid var(--ctp-surface0) !important;
    }

    /* =====================
     TOP MENU STRIP
     ===================== */
    .right-menu-text-icon {
        background-color: var(--ctp-mantle) !important;
        border-bottom: 3px solid var(--ctp-blue) !important;
        color: var(--ctp-text) !important;
    }

    .right-menu-text-icon:hover {
        background-color: var(--ctp-surface0) !important;
    }

    /* =====================
     CONTEXT MENU
     ===================== */
    .context-menu-list {
        background-color: var(--ctp-base) !important;
        border: 1px solid var(--ctp-surface0) !important;
        box-shadow: 0 8px 24px rgba(0, 0, 0, 0.6) !important;
    }

    .context-menu-item-label {
        color: var(--ctp-text) !important;
    }

    .context-menu-item:hover {
        background-color: var(--ctp-surface1) !important;
    }

    .context-menu-item:hover .context-menu-item-label {
        color: #ffffff !important;
    }

    .context-menu-item.context-menu-danger .context-menu-item-label {
        color: var(--ctp-red) !important;
    }

    /* =====================
     WISHLIST
     ===================== */
    #top-wishlist-div,
    #wishlist-container {
        background-color: var(--ctp-mantle) !important;
        color: var(--ctp-text) !important;
    }

    #wishlist-container h6 {
        background-color: var(--ctp-base) !important;
        color: #ffffff !important;
        border-bottom: 1px solid var(--ctp-surface0) !important;
    }

    #wishlist-container li {
        background-color: var(--ctp-base) !important;
        border-bottom: 1px solid var(--ctp-surface0) !important;
    }

    .wishlist-title {
        color: #ffffff !important;
    }

    .wishlist-size,
    .wishlist-date {
        color: var(--ctp-subtext0) !important;
    }

    .wishlist-download {
        background-color: var(--ctp-surface0) !important;
        color: var(--ctp-mauve) !important;
        border: 1px solid var(--ctp-surface1) !important;
    }

    .wishlist-download:hover {
        background-color: var(--ctp-surface1) !important;
        color: #ffffff !important;
    }

    /* =====================
     MODALS
     ===================== */
    .reveal-modal,
    .modal-header,
    .modal-body {
        background-color: var(--ctp-base) !important;
        color: var(--ctp-text) !important;
    }

    .modal-header {
        border-bottom: 1px solid var(--ctp-surface0) !important;
    }
    /* =====================
   SIZE COLUMN – DOTTED FIX (CORRECT)
   ===================== */
    .content-item-size {
        background: transparent !important;
        color: var(--ctp-text) !important;
        position: relative;
    }

    /* recreate Seedr dotted separator */
    .content-item-size::after {
        content: "";
        position: absolute;
        left: 0;
        right: 0;
        bottom: -1px;
        border-bottom: 1px dashed rgba(205, 214, 244, 0.45);
        pointer-events: none;
    }

    /* hover state stays aligned with row */
    .content-item:hover .content-item-size {
        background: transparent !important;
        color: #ffffff !important;
    }

    .content-item:hover .content-item-size::after {
        border-bottom-color: rgba(255, 255, 255, 0.7);
    }
    /* =====================
   FINAL WHITE UI FIXES
   ===================== */
    /* ---------
   Top action icons (search / create folder / sort)
   --------- */
    .files-nav-icon,
    .files-nav-icon i,
    #files-nav-search,
    #files-nav-create-folder,
    #files-nav-sort {
        background-color: #313244 !important;
        color: #cdd6f4 !important;
        border: 1px solid #45475a !important;
    }

    .files-nav-icon:hover,
    .files-nav-icon:hover i {
        background-color: #45475a !important;
        color: #ffffff !important;
    }

    /* ---------
   Link detection popup (white box)
   --------- */
    #link-results,
    #link-results-empty {
        background-color: #1e1e2e !important;
        color: #a6adc8 !important;
        border: 1px solid #313244 !important;
        box-shadow: 0 8px 24px rgba(0, 0, 0, 0.6) !important;
    }

    /* Arrow above popup */
    #link-results::before {
        border-bottom-color: #1e1e2e !important;
    }

    /* ---------
   Upload / plus button (sometimes flashes white)
   --------- */
    #upload-button,
    #upload-button i {
        background-color: #313244 !important;
        color: #cdd6f4 !important;
        border: 1px solid #45475a !important;
    }

    #upload-button:hover {
        background-color: #45475a !important;
        color: #ffffff !important;
    }
    /* =====================
   FINAL REMAINING LIGHT UI FIX
   ===================== */
    /* ---------------------
   MAIN DROPDOWN MENU
   --------------------- */
    #main-drop,
    #main-drop ul {
        background-color: #1e1e2e !important;
        color: #cdd6f4 !important;
        border: 1px solid #313244 !important;
    }

    /* menu items */
    #main-drop li {
        background-color: #1e1e2e !important;
        color: #cdd6f4 !important;
    }

    /* separators */
    #main-drop .menu-separator {
        border-bottom: 1px solid #313244 !important;
    }

    /* hover */
    #main-drop li:hover {
        background-color: #45475a !important;
        color: #ffffff !important;
    }

    /* icons inside menu */
    #main-drop i,
    #main-drop svg {
        color: #cdd6f4 !important;
        fill: #cdd6f4 !important;
    }

    /* ---------------------
   FORCE DARK "BECOME PREMIUM"
   --------------------- */
    #subscription-menu-item {
        background-color: #cba6f7 !important;
        color: #1e1e2e !important;
    }

    #subscription-menu-item i {
        color: #1e1e2e !important;
    }

    /* hover keeps contrast */
    #subscription-menu-item:hover {
        background-color: #b4befe !important;
        color: #1e1e2e !important;
    }

    /* ---------------------
   CLIPBOARD / DOCUMENT ICON
   --------------------- */
    /* icon container */
    #link-paste-icon {
        background-color: #313244 !important;
        border-right: 1px solid #45475a !important;
    }

    /* SVG / image-based icon */
    #link-paste-icon img,
    #link-paste-icon svg {
        filter: invert(1) brightness(0.9) !important;
    }

    /* ensure no white flash */
    #link-upload-text {
        background-color: #181825 !important;
    }
    /* =====================
   HAMBURGER ICON – FINAL FIX
   ===================== */
    /* base state */
    #menu-drop,
    #menu-drop i {
        background-color: #313244 !important;
        color: #cdd6f4 !important;
    }

    /* hover state (this is the missing one) */
    #menu-drop:hover,
    #menu-drop:hover i {
        background-color: #45475a !important;
        color: #ffffff !important;
    }

    /* active / opened state */
    #menu-drop.active,
    #menu-drop.active i {
        background-color: #45475a !important;
        color: #ffffff !important;
    }

    /* prevent header hover from forcing white */
    header:hover #menu-drop,
    header:hover #menu-drop i {
        background-color: #45475a !important;
        color: #ffffff !important;
    }
}

/* ==UserStyle==
@name Claude Catppuccin
@namespace github.com/catppuccin/userstyles/styles/claude
@homepageURL https://github.com/catppuccin/userstyles/tree/main/styles/claude
@version 2025.09.06
@updateURL https://github.com/catppuccin/userstyles/raw/main/styles/claude/catppuccin.user.less
@supportURL https://github.com/catppuccin/userstyles/issues?q=is%3Aopen+is%3Aissue+label%3Aclaude
@description Soothing pastel theme for Claude
@author Catppuccin
@license MIT

@preprocessor less
@var select lightFlavor "Light Flavor" ["latte:Latte*", "frappe:Frappé", "macchiato:Macchiato", "mocha:Mocha"]
@var select darkFlavor "Dark Flavor" ["latte:Latte", "frappe:Frappé", "macchiato:Macchiato", "mocha:Mocha*"]
@var select accentColor "Accent" ["rosewater:Rosewater", "flamingo:Flamingo", "pink:Pink", "mauve:Mauve*", "red:Red", "maroon:Maroon", "peach:Peach", "yellow:Yellow", "green:Green", "teal:Teal", "blue:Blue", "sapphire:Sapphire", "sky:Sky", "lavender:Lavender", "subtext0:Gray"]
==/UserStyle== */

@import "https://userstyles.catppuccin.com/lib/lib.less";

@-moz-document domain("claude.ai") {
  :root[data-mode="dark"] {
    #catppuccin(@darkFlavor);
  }
  :root[data-mode="light"],
  /* Login page */
  [data-theme="claude"][data-mode="light"] {
    #catppuccin(@lightFlavor);
  }

  #catppuccin(@flavor) {
    #lib.palette();
    #lib.defaults();

    --accent-brand: #hslify(@accent)[];
    --accent-main-000: #hslify(@accent)[];
    --accent-main-100: #hslify(@accent)[];
    --accent-main-200: #hslify(@accent)[];
    --accent-main-900: #hslify(@accent)[];
    --accent-pro-000: #hslify(@accent)[];
    --accent-pro-100: #hslify(@accent)[];
    --accent-pro-200: #hslify(@accent)[];
    --accent-pro-900: #hslify(@accent)[];
    --accent-secondary-000: #hslify(@accent)[];
    --accent-secondary-100: #hslify(@accent)[];
    --accent-secondary-200: #hslify(@accent)[];
    --accent-secondary-900: #hslify(@accent)[];
    --bg-000: #hslify(@mantle)[];
    --bg-100: #hslify(@base)[];
    --bg-200: #hslify(@base)[];
    --bg-300: #hslify(@surface0)[];
    --bg-400: #hslify(@surface1)[];
    --bg-500: #hslify(@surface2)[];
    --border-000: #hslify(@base)[];
    --border-100: #hslify(@crust)[];
    --border-200: #hslify(@crust)[];
    --border-300: #hslify(@overlay0)[];
    --border-400: #hslify(@overlay1)[];
    --border-500: #hslify(@overlay2)[];
    --danger-000: #hslify(@maroon)[];
    --danger-100: #hslify(@red)[];
    --danger-200: #hslify(@red)[];
    --danger-900: #hslify(@pink)[];
    --oncolor-000: #hslify(darken(@mantle, 5%))[];
    --oncolor-100: #hslify(@mantle)[];
    --oncolor-200: #hslify(@mantle)[];
    --oncolor-300: #hslify(lighten(@mantle, 5%))[];
    --text-000: #hslify(darken(@text, 5%))[];
    --text-100: #hslify(@text)[];
    --text-200: #hslify(@text)[];
    --text-300: #hslify(@subtext0)[];
    --text-400: #hslify(@subtext1)[];
    --text-500: #hslify(@subtext0)[];

    /* Side bar */
    nav.\!bg-bg-200 {
      background: @mantle !important;
    }

    /* Code blocks */
    .code-block__code {
      background: @crust !important;

      code {
        /* Default color */
        span {
          color: @text !important;
        }

        /* Green */
        span.token[style="color: rgb(152, 195, 121);"] {
          color: @green !important;
        }

        /* Yellow */
        span.token[style="color: rgb(209, 154, 102);"] {
          color: @yellow !important;
        }

        /* Blue */
        span.token[style="color: rgb(97, 175, 239);"] {
          color: @blue !important;
        }

        /* Red */
        span.token[style="color: rgb(224, 108, 117);"] {
          color: @red !important;
        }

        /* Magenta */
        span.token[style="color: rgb(198, 120, 221);"] {
          color: @mauve !important;
        }

        /* Gray */
        span.token[style="color: rgb(171, 178, 191);"] {
          color: @overlay2 !important;
        }
      }
    }

    /* Inline code */
    code.text-danger-000 {
      color: @accent !important;
    }

    /* Tooltip */
    div.bg-black\/80 {
      background: @crust !important;
    }
    div.text-white {
      color: @text !important;
    }

    /* Logo */
    div.text-\[\#D97757\] {
      color: @accent !important;
    }

    /* "New chat" plus icon */
    div.text-always-white svg {
      color: @base !important;
    }

    /* "Subscribe to Pro" and "Create Team Account" buttons */
    button.bg-accent-pro-100 > span.inline-block {
      color: @base !important;
    }

    /* Pay monthly/annually buttons */
    button.bg-accent-secondary-900 {
      span.text-text-000 {
        color: @base !important;
      }
      span.text-text-300 {
        color: @surface2 !important;
      }
      div.bg-accent-secondary-000 {
        background: darken(@accent, 15%) !important;
      }
    }

    /* Delete conversation button */
    div.\!text-danger-000.\[\&\[data-highlighted\]\]\:bg-danger-900[data-highlighted] {
      background: fade(@red, 20%) !important;
    }

    /* Checkbox */
    input[type="checkbox"] + div.bg-bg-000 {
      background: @text !important;
    }

    /* "Add writing example" button */
    button.\!text-accent-secondary-100.bg-accent-secondary-900 {
      color: @base !important;
      &:hover {
        color: @text !important;
      }
    }

    /* "Describe your style" list */
    button.\!bg-accent-secondary-900.text-accent-secondary-000 {
      color: @base !important;
    }

    /* "Be as descriptive as possible..." helper text */
    div.bg-accent-secondary-900.text-text-200 {
      color: @base !important;
    }

    /* "Preview with an example..." pills */
    button.hover\:bg-accent-secondary-900.hover\:text-accent-secondary-000 {
      color: @accent !important;
      &:hover {
        color: @base !important;
      }
    }

    /* "Previewing with ..." dropdown button */
    button.\!text-accent-secondary-100.hover\:\!bg-accent-secondary-900 {
      color: @accent !important;
      &:hover {
        color: @base !important;
      }
    }
  }
}

#hslify(@color) {
  @raw: e(%("%s %s% %s%", hue(@color), saturation(@color), lightness(@color)));
}

